mapper.select:
select o.gateway_out_config_id,a.agree_name,o.enterprise_code,o.rate,o.carrier_type, case o.customer_type when 1 then '预付费' when 2 then '后付费' end as customer_type_str, o.enterprise_ip,cus.enter_contact, cus.customer_name,o.sp_access_code,o.remainder_msg_num,o.is_used,o.isabled, case o.is_used when 1 then '有效' when 0 then '无效' when 2 then '无效' end as is_used_str, case o.carrier_type when 9 then '网关分流' else c.channel_name end as channel_name from tbl_gateway_out_config o LEFT JOIN tbl_sp_gateway_config s on s.gateway_out_config_id = o.gateway_out_config_id LEFT JOIN tbl_gateway_access_config c on s.config_id = c.config_id LEFT JOIN tbl_carrier_agree a on o.carrier_agree_id = a.carrier_agree_id LEFT JOIN tbl_customer cus on o.customer_id = cus.customer_id where o.isabled = 1
and c.config_id = #{configId,jdbcType=INTEGER}
and o.customer_type = #{customerType,jdbcType=INTEGER}
and o.carrier_agree_id = #{carrierAgreeId,jdbcType=INTEGER}
and o.sp_access_code like concat('%',#{spAccessCode,jdbcType=VARCHAR},'%')
and o.enterprise_ip like concat('%',#{enterpriseIp,jdbcType=VARCHAR},'%')
and o.enterprise_code like concat('%',#{enterpriseCode,jdbcType=VARCHAR},'%')
and cus.enter_contact like concat('%',#{enterContact,jdbcType=VARCHAR},'%')
and cus.customer_name like concat('%',#{customerName,jdbcType=VARCHAR},'%')
and o.carrier_type = #{carrierType,jdbcType=INTEGER}
and o.is_used in (0,2)
and o.is_used = #{isUsed,jdbcType=INTEGER}
group by o.gateway_out_config_id ORDER by o.create_time desc limit #{offset,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select count(DISTINCT o.gateway_out_config_id) from tbl_gateway_out_config o LEFT JOIN tbl_sp_gateway_config s on s.gateway_out_config_id = o.gateway_out_config_id LEFT JOIN tbl_gateway_access_config c on s.config_id = c.config_id LEFT JOIN tbl_carrier_agree a on o.carrier_agree_id = a.carrier_agree_id LEFT JOIN tbl_customer cus on o.customer_id = cus.customer_id where o.isabled = 1
and c.config_id = #{configId,jdbcType=INTEGER}
and o.customer_type = #{customerType,jdbcType=INTEGER}
and o.carrier_agree_id = #{carrierAgreeId,jdbcType=INTEGER}
and o.sp_access_code like concat('%',#{spAccessCode,jdbcType=VARCHAR},'%')
and o.enterprise_ip like concat('%',#{enterpriseIp,jdbcType=VARCHAR},'%')
and o.enterprise_code like concat('%',#{enterpriseCode,jdbcType=VARCHAR},'%')
and cus.enter_contact like concat('%',#{enterContact,jdbcType=VARCHAR},'%')
and cus.customer_name like concat('%',#{customerName,jdbcType=VARCHAR},'%')
and o.carrier_type = #{carrierType,jdbcType=INTEGER}
and o.is_used in (0,2)
and o.is_used = #{isUsed,jdbcType=INTEGER}

mapper.update:
update tbl_gateway_out_config
set remainder_msg_num = #{numAfterRecharge,jdbcType=INTEGER}
where enterprise_code = #{enterpriseCode,jdbcType=VARCHAR} and isabled = 1
