mapper.insert:

insert into tbl_normal_blacklist (normal_blacklist_id, phone_number, blacklist_source, 
  remarks, isabled, join_time
  )
values (#{normalBlacklistId,jdbcType=INTEGER}, #{phoneNumber,jdbcType=VARCHAR}, #{blacklistSource,jdbcType=INTEGER}, 
  #{remarks,jdbcType=VARCHAR}, #{isabled,jdbcType=INTEGER}, #{joinTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(distinct phone_number),normal_blacklist_id, phone_number, blacklist_source, remarks, isabled, join_time, case when blacklist_source = 1 then '回复加黑' when blacklist_source = 2 then '手动添加' when blacklist_source = 3 then '其他搜集' end as blacklist_source_str from tbl_normal_blacklist where isabled = 1
and phone_number like concat('%',#{phoneNumber,jdbcType=VARCHAR},'%')
and join_time >= #{startTime,jdbcType=VARCHAR}
and join_time <= #{endTime,jdbcType=VARCHAR}
group by phone_number order by join_time desc limit #{offset,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select count(distinct phone_number) from tbl_normal_blacklist
where isabled = 1

  and phone_number like concat('%',#{phoneNumber,jdbcType=VARCHAR},'%')


  and join_time >= #{startTime,jdbcType=VARCHAR}


  and join_time <= #{endTime,jdbcType=VARCHAR}
