mapper.update:
update tbl_sys_intercept_keyword
set keyword = #{keyword,jdbcType=VARCHAR},
  isabled = #{isabled,jdbcType=INTEGER},
  join_time = #{joinTime,jdbcType=TIMESTAMP}
where sys_intercept_keyword_id = #{sysInterceptKeywordId,jdbcType=INTEGER}

update tbl_sys_intercept_keyword
set isabled = 0
where isabled = 1 and keyword = #{keyword,jdbcType=VARCHAR}

mapper.select:
select count(distinct keyword),sys_intercept_keyword_id,keyword,isabled,join_time
from tbl_sys_intercept_keyword
where isabled = 1

  and keyword like concat('%',#{keyword,jdbcType=VARCHAR},'%')


  and join_time >= #{startTime,jdbcType=VARCHAR}


  and join_time <= #{endTime,jdbcType=VARCHAR}

group by keyword
order by join_time desc
limit #{offset,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select count(distinct keyword) from tbl_sys_intercept_keyword
where isabled = 1

  and keyword like concat('%',#{keyword,jdbcType=VARCHAR},'%')


  and join_time >= #{startTime,jdbcType=VARCHAR}


  and join_time <= #{endTime,jdbcType=VARCHAR}
